import React, { useState } from "react";
import { getOpenWeatherMapForecast } from "../services/openWeatherMap";

import "./App.css";
import LocationForm from "./Components/LocationForm";
import Dates from "./Components/Dates";

function App() {

  const [weather, setWeather] = useState({
    city: "",
    country: "",
    temperature: "",
    temp_min: "",
    temp_max: "",
    feels_like: "",
    humidity: "",
    description: "",
    error: "",
  });
  const getWeather = (e) => {
    e.preventDefault();
    const city = e.target.elements.city.value;
    const country = e.target.elements.country.value;
    const kelvin = 273;

    if (city && country) {
      getOpenWeatherMapForecast(city)
        .then((response) => response.json)
        .then((result) => {
          setWeather({

            temperature: result.list[0].main.temp - kelvin,
            temp_min: "",
            temp_max: "",
            feels_like: "",
            humidity: "",
            description: "",
            error: "",
          });

        });
    }
  };

  return (
    <div className="App">
      <LocationForm />
      <Dates />
      <Weather />
    </div>
  );
}

export default App;
