import React, { useEffect, useState } from "react";

function Temperature(props) {
  const [temperature, setTemperature] = useState(null);
  const [tempType, setTempType] = useState("celcius");

  useEffect(() => {
    setTemperature(props.temperature);
  }, [props]);

  const celciusToFahrenheitAndBack = () => {
    if (tempType === "celcius") {
      setTempType("fahrenheit");
      setTemperature((temperature * 9/5) + 32);
      console.log(tempType);
    } else {
      setTempType("celcius");
      setTemperature(t(temperature * 9/5) + 32);
      console.log(tempType);
    }
  };


  return (
    <div onClick={celciusToFahrenheitAndBack}>{ props.temperature &&
      <p>the temperature is :{temperature},{tempType}</p>}
    </div>
  );
}

export default Temperature;
