import React,{useState} from "react";

function Temperature(props) {
  const [temperature, setTemperature] = useState(null);
  const [tempType, setTempType] = useState("celcius");

useEffect(() => {
setTemperature(props.temp)
}, [props])

  const celciusToFahrenheitAndBack = () => {
    if (tempType === "celcius") {
      setTempType("fahrenheit");
      setTemperature((temperature * 9) / 5 + 32);
    } else {
      setTempType("celcius");
      setTemperature(temperature);
    }
  };
  console.log(props.temperature);

  return <div onClick={celciusToFahrenheitAndBack}>the temperature is :{props.temperature}</div>;
}

export default Temperature;
