import React,{useState} from 'react'



export default function Location() {
    const [city,setCity] = useState("");
    const[country,setCountry] =useState("");


    const getWeatherDetails =(e)=>{
            setCity(e.target.value);
            setCountry(e.target.value);
    }
    return (
        <div>
            <input type="text" className="city" placeholder="City" value={city} />
            <input type="text" classame="country" placeholder="Country" value={country} />
            <button onClick={getWeatherDetails}>Get Weather Details</button>
        </div>
    )
}
