import React, { useState } from "react";
import "./App.css";
import LocationForm from "./Components/LocationForm";
import Dates from "./Components/Dates";
import Temperature from "./Components/Temperature";
import { getOpenWeatherMapForecast } from "./services/openWeatherMap";

function App() {
  const [weatherForcast, setWeatherForcast] = useState({
    temperature: "",
    temp_min: "",
    temp_max: "",
    feels_like: "",
    humidity: "",
    description: "",
    error: "",
  });
  const [city, setCity] = useState("");
  const [country, setCountry] = useState("");

  const handleCityChange = (event) => {
    console.log(event.target.value);
    setCity(event.target.value)
  };
  const handleCountryChange = (event) => {
    console.log(event.target.value);
    setCountry(event.target.value)
  };

  const getWeather = (e) => {
    e.preventDefault();
    // console.log(city);
    console.log(e);
    // const city = e.target.elements.city.value;
    // const country = e.target.elements.country.value;

    const kelvin = 273;

    if (city && country) {
      getOpenWeatherMapForecast(city)
        .then((response) => response.json)
        .then((result) => {
          setWeatherForcast({
            temperature: result.list[0].main.temp ,
            temp_min: "",
            temp_max: "",
            feels_like: "",
            humidity: "",
            description: "",
            error: "",
          });
        handleCityChange()
        });
    } else {
      setWeatherForcast({
        temperature: "",
        temp_min: "",
        temp_max: "",
        feels_like: "",
        humidity: "",
        description: "",
        error: "Please enter city and country",
      });
    }
  };

  return (
    <div className="App">
      <LocationForm
        handleCityChange={handleCityChange}
        handleCountryChange={handleCountryChange}
        getWeather={getWeather}
      />
      <Dates />
      <Temperature temperature={weatherForcast.temperature} />
    </div>
  );
}

export default App;
