import React, { useState } from "react";
import styles from "./App.module.css";
import Title from "./Components/Title";
import LocationForm from "./Components/LocationForm";
import Humidity from "./Components/Humidity";

import Temperature from "./Components/Temperature";
import { getOpenWeatherMapForecast } from "./services/openWeatherMap";



function App() {
  const [weatherForcast, setWeatherForcast] = useState({
    date_time: "",
    temperature: "",
    temp_min: "",
    temp_max: "",
    feels_like: "",
    humidity: "",
    description: "",
    error: "",
  });
  const [city, setCity] = useState("");
  const [country, setCountry] = useState("");

  const handleCityChange = (event) => {
    // console.log(event.target.value);
    setCity(event.target.value);
  };
  const handleCountryChange = (event) => {
    // console.log(event.target.value);
    setCountry(event.target.value);
  };
  let tempForcastNext4Days =[];
  const getWeather = (e) => {
    e.preventDefault();

    // console.log(e);
    // const city = e.target.elements.city.value;
    // const country = e.target.elements.country.value;

    const kelvin = 273;

    if (city && country) {
      getOpenWeatherMapForecast(city, country)
        .then((response) => response.json())
        .then((result) => {
          console.log(result);
          setWeatherForcast({
            date_time: result.list[0].dt_txt,
            temperature: result.list[0].main.temp - kelvin,
            temp_min: result.list[0].main.temp_min - kelvin,
            temp_max: result.list[0].main.temp_max - kelvin,
            feels_like: result.list[0].main.feels_like - kelvin,
            humidity: result.list[0].main.humidity,
            description: result.list[0].weather[0].description,
            error: "",
          });


        });
    } else {
      setWeatherForcast({
        temperature: "",
        temp_min: "",
        temp_max: "",
        feels_like: "",
        humidity: "",
        description: "",
        error: "Please enter city and country",
      });
    }

  };

  return (
    <div className={styles.app}>
      <div className={styles.title}>
        <Title />
      </div>
      <div className={styles.weather_info}>
        <LocationForm
          handleCityChange={handleCityChange}
          handleCountryChange={handleCountryChange}
          getWeather={getWeather}
        />
        <Temperature temperature={weatherForcast.temperature} />
        <Temperature temperature={weatherForcast.temp_max} />
        <Temperature temperature={weatherForcast.temp_max} />
        <Temperature temperature={weatherForcast.temp_max} />
        <div>
        {tempForcastNext4Days.map((temp,index) => (
          <Temperature temperature={<p key={index}>Next-Day:{index+1} :<span>{temp}</span></p>} />
        ))}
        </div>


        <Humidity humidity={weatherForcast.humidity} />

        <p>{weatherForcast.description}</p>
      </div>
    </div>
  );
}

export default App;
