import React, { useState } from "react";
import "./App.css";
import LocationForm from "./Components/LocationForm";
import Dates from "./Components/Dates";
import { getOpenWeatherMapForecast } from "./services/openWeatherMap";

function App() {
  const [weatherForcast, setWeatherForcast] = useState({
    temperature: "",
    temp_min: "",
    temp_max: "",
    feels_like: "",
    humidity: "",
    description: "",
    error: "",
  });
  const getWeather = (e) => {
    e.preventDefault();
    const city = e.target.elements.city.value;
    const country = e.target.elements.country.value;
    const kelvin = 273;

    if (city && country) {
      getOpenWeatherMapForecast(city)
        .then((response) => response.json)
        .then((result) => {
          setWeatherForcast({
            temperature: result.list[0].main.temp - kelvin,
            temp_min: "",
            temp_max: "",
            feels_like: "",
            humidity: "",
            description: "",
            error: "",
          });
        });

    } else {
      setWeatherForcast({
        temperature: "",
        temp_min: "",
        temp_max: "",
        feels_like: "",
        humidity: "",
        description: "",
        error: "Please enter city and country",
      });
